function enable_disable_fm(selected_option) {
var
class_name = $j(selected_option).attr("class");

var length =
class_name.length;

var class_id = parseInt(class_name.substring(length
- 1, length))

var row_id = class_id;

var disable = true;

var row_1 =
'[class^="FamilyMember"][class*="Children1"]';
var row_2 =
'[class^="FamilyMember"][class*="Children2"]';
var row_3 =
'[class^="FamilyMember"][class*="Children3"]';
var row_4 =
'[class^="FamilyMember"][class*="Children4"]';
var row_5 =
'[class^="FamilyMember"][class*="Children5"]';

var xx = row_1;
var
selected_value = $j(selected_option).find(":selected").text()


switch
(selected_value) {
case "P":
case "Y":
disable = false;
break;

}


switch
(row_id) {
case 1:
xx = row_1;
break;
case 2:
xx = row_2;
break;
case 3:
xx =
row_3;
break;
case 4:
xx = row_4;
break;
case 5:
xx = row_5;
break;
}


$j(xx).each(function () {
var group = $j(this);

if
(class_name.indexOf('Children') == -1) {

group.find("input").attr("disabled", disable);
group.find('select').attr("disabled", disable);

}
else if
(class_name.indexOf('GrandChildren') == -1) {
group.find("input").attr("disabled", disable);
}

});

}

